const randomReactions = ["👍", "😄", "❤️", "🎉", "🔥", "😎", "👏", "💯"];


module.exports = {

  config: {

    name: "bot",

    version: "1.4",

    author: "karan jalvanshi",

    countDown: 0,

    role: 0,

    shortDescription: {

      en: 'Send random Hindi messages with reactions.'

    },

    longDescription: {

      en: "This command sends random messages in Hindi along with random reactions whenever a user types 'bot', with or without a prefix."

    },

    category: "fun",

    guide: {

      en: "{p}bot"

    }

  },


  onStart: async () => {},


  onChat: async ({ api, event, usersData }) => {

    try {

      const messageBody = event.body.toLowerCase().trim();


      if (messageBody.startsWith("bot")) {

        const id = event.senderID;

        const userData = await usersData.get(id);


        if (!userData) {

          return api.sendMessage("User data not found.", event.threadID, event.messageID);

        }


        const mention = [{ id, tag: userData.name }];

        const randomIndex = Math.floor(Math.random() * randomMessages.length);

        const randomReactionIndex = Math.floor(Math.random() * randomReactions.length);

        const randomMessage = randomMessages[randomIndex];

        const randomReaction = randomReactions[randomReactionIndex];

 api.setMessageReaction(randomReaction, event.messageID, () => {}, true);


        await api.sendMessage({

          body: `${userData.name} ${randomMessage}`,

          mentions: mention

        }, event.threadID, event.messageID);

      }

    } catch (error) {

      console.error('Error sending random message:', error);

      api.sendMessage("moye moye.", event.threadID, event.messageID);

    }

  }

};
